//! Variables (Jquery Type)
htmlDocument = $("html");
const headerMenu = $(".header__menu"),
  headerMenuOpener = $(".header__content-menu"),
  headerMenuCloser = $(".header__menu-close"),
  popularSections = $(".popular__content-body"),
  popularSectionButtons = $(".popular__content-button"),
  openHeaderMenu = () => {
    headerMenu.animate({ right: 0 }, 1e3), htmlDocument.css("overflow", "hidden");
  },
  closerHeaderMenu = () => {
    headerMenu.animate({ right: "-100vw" }, 1e3), htmlDocument.css("overflow", "auto");
  },
  visiblePopularSection = (e) => {
    let o = e.target.className.slice(24),
      n = $(`.popular__content-body.${o}`); //! Changing Buttons Style
    popularSectionButtons.each((e, o) => {
      o.style.backgroundColor = "transparent";
    }),
      (e.target.style.backgroundColor = "#ffffff3b"), //! Changing Sections Style
      popularSections.each((e, o) => {
        (o.style.display = "none"), (o.style.opacity = "0");
      }),
      n.css("display", "flex"),
      n.animate({ opacity: "1" });
  }; //! Calling Functions
headerMenuOpener.on("click", openHeaderMenu),
  headerMenuCloser.on("click", closerHeaderMenu),
  popularSectionButtons.on("click", visiblePopularSection);
